import React, { useState } from 'react';
import NavTabs from './NavTabs';
import Home from './pages/Home';
import About from './pages/About';
import Blog from './pages/Blog';
import Contact from './pages/Contact';

export default function PortfolioContainer() {
  const [currentPage, setCurrentPage] = useState('Home');

  // TODO: Add a comment describing the functionality of this method
  // Change the content to show depending on the selection.
  const renderPage = () => {
    if (currentPage === 'Home') {
      return <Home />; // return Home component
    }
    if (currentPage === 'About') {
      return <About />;
    }
    if (currentPage === 'Blog') {
      return <Blog />;
    }
    return <Contact />;
  };

  const handlePageChange = (page) => setCurrentPage(page); // setCurrentPageを使うFunctionは必ずここで定義する 

  return (
    <div>
      {/* // TODO: Add a comment describing what we are passing as props // variable currentPage & function handlePageChange // */}
      <NavTabs currentPage={currentPage} handlePageChange={handlePageChange} />
      {/* // TODO: Add a comment explaining what is happening on the following line // Content in this tab will be changed// */}
      {renderPage()}
    </div>
  );
}
