import React from 'react';

// TODO: Add a comment explaining what is being passed to this "child" component as props
// props.count = state count
// props.handleIncrement = function to increment state count
// props.handleDecrement = function to decrement state count
function CardBody(props) {
  return (
    <div className="card-body">
      <p className="card-text">Click Count: {props.count}</p>
      <button
        type="button"
        className="btn btn-primary"
        onClick={props.handleIncrement}
      >
        Increment
      </button>{' '}
      <button
        type="button"
        className="btn btn-danger"
        onClick={props.handleDecrement}
      >
        Decrement
      </button>
    </div>
  );
}

export default CardBody;
